import React, {useState, useEffect} from 'react';

export const UnsplashImage = () => {
	const [activeImageUrl, setActiveImageUrl] = useState('')
	const [activeImageObject, setActiveImageObject] = useState({})
	const [activeImageLocation, setActiveImageLocation] = useState('')
	const [imagesList, setImageList] = useState([])

	const fetchRandomImage = (list) => {
		let num = Math.floor(Math.random() * 30);
		let randomImage = list[num]
		setActiveImageObject(randomImage);
		console.log("Active: ", randomImage)
		if (randomImage != null) {
			let url = randomImage.urls.regular
			setActiveImageUrl(url)
		}
	};

	const fetchImagesList = async () => {
		try {
			const response = await fetch(
				'https://api.unsplash.com/photos/?query=nature&client_id=pmTNZxpE0TQ6pRwUqnkTL8uWNhzcEWYQzPdzd8NtmPo&per_page=30&order_by=popular')
			const data = await response.json()

			setImageList(data)
		}
		catch {
			console.error("Error fetching images from API, Please try again!")
		}
	};

	useEffect(() => {
		if (imagesList.length > 0) {
		  fetchRandomImage(imagesList);
		  let image_id = activeImageObject.id;
		  fetchActiveImageLocation(image_id);
		}
	  }, [imagesList, activeImageObject]);

	const handlePlayClick = () => {
		fetchImagesList();
	};

	const fetchActiveImageLocation = async (image_id) => {
		console.log(image_id);
		try {
			const response = await fetch('https://api.unsplash.com/photos/' + image_id, {
				headers: {
					'Authorization': 'Client-ID pmTNZxpE0TQ6pRwUqnkTL8uWNhzcEWYQzPdzd8NtmPo'
				}
			})
			const data = await response.json()

			console.log('location_fetch:', data)
			if (activeImageObject.location != null) {
				setActiveImageLocation(activeImageLocation)
			}

		}
		catch {
			console.error("There was an error fetching location, please try again!")
		}
	}

	return (
		<div className='ImageSection'>
			<p>Direction: Try and guess where the picture is taken</p>
			<button onClick={handlePlayClick}>Play</button>
			<img src={activeImageUrl} alt='' className='randomImage' />
		</div>
	);
}
